<?phpnamespace App\Http\Middleware;use Closure;use Illuminate\Support\Facades\Auth;class IsAdmin{    /**     * Handle an incoming request.     *     * @param  \Illuminate\Http\Request  $request     * @param  \Closure  $next     * @return mixed     */    public function handle($request, Closure $next)    {        // if (Auth::check() && Auth::user()->role_id == '1') {        //     return $next($request);        // }        // else        // {        //     return redirect('/partner/dashboard');        // }                if (Auth::check()) {            if (Auth::user()->role_id == '1') {                return $next($request);            }            // Handle role-based redirection            return redirect('/partner/dashboard');        }        // Handle domain-based redirection if not authenticated        if ($request->getHost() == 'partners.famoryapp.com') {            return redirect()->route('login');        }        if ($request->getHost() == 'admin.famoryapp.com') {            return redirect()->route('admin.login');        }                       }}